import React, {useState} from 'react';

import Expenses from './components/expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense'; 

const DUMMY_EXPENSES = [
  { title: 'MAchinery Tools', amount: 2099.0, date: new Date(2021, 5, 24) },
  { title: 'Car Insurance', amount: 2099.0, date: new Date(2021, 5, 24) },
  { title: 'Painting cost', amount: 2099.0, date: new Date(2021, 5, 24) },
  { title: 'Transorming cost', amount: 2099.0, date: new Date(2021, 5, 24) }
]

function App() {

  const[expenses, setExpenses] = useState(DUMMY_EXPENSES);

  function addExpenseHandler(arr){
    setExpenses((prevExpenses)=>{
      return [expenses, ...prevExpenses];
    })
  }

return (
  <div>
    <NewExpense onAddExpense={addExpenseHandler}></NewExpense>
    <Expenses item={DUMMY_EXPENSES}></Expenses>
  </div>
);
}

export default App;
