import "./ExpenseItem.css";

function ExpenseItem(props) {

    const month = z.toLocaleString('en-US', { month: 'long' });
    const day = z.toLocaleString('en-US', { day: '2-digit' });
    const year = z.getFullYear();

    return (
        <div className="expense-item">
            <div>
                <div>{props.month}</div>
                <div>{props.year}</div>
                <div>{props.day}</div>
            </div>
            <div className="expense-item__description">
                <h2 className="expense-item h2">{props.x}</h2>
                <div className="expense-item__price">Rs{props.y}</div>
            </div>
        </div>
    );
}

export default ExpenseItem;
